# Here we are training the classifier, taking 80% of the examples as training data and 20% as test data
train_data, test_data = train_test_split(examples, test_size=0.2, random_state=42)
train_dataset = Dataset.from_list(train_data)
test_dataset = Dataset.from_list(test_data)

# Use tokenizer to prepare for classifier model input
tokenizer = DistilBertTokenizerFast.from_pretrained("distilbert-base-uncased")

def preprocess(examples):
    return tokenizer(examples["text"], truncation=True, padding=True)

# apply tokenizer to all entries
train_dataset = train_dataset.map(preprocess, batched=True)
test_dataset = test_dataset.map(preprocess, batched=True)

config = DistilBertConfig.from_pretrained("distilbert-base-uncased", num_labels=2)
config.dropout = 0.2
config.attention_dropout = 0.2
# Load DistilBERT model, Bidirectional Encoder Representations from Transformers, for sequence classification, can help understand the context of words and figure out relationships
model = DistilBertForSequenceClassification.from_pretrained("distilbert-base-uncased", config=config)


# Use accuracy metric for evaluation (hugging face)
accuracy = evaluate.load("accuracy")
precision = evaluate.load("precision")
recall = evaluate.load("recall")
f1 = evaluate.load("f1")

def compute_metrics(eval_pred):
    logits, labels = eval_pred
    preds = np.argmax(logits, axis=1)
    f2 = fbeta_score(labels, preds, beta=2)
    acc = accuracy.compute(predictions=preds, references=labels)["accuracy"]
    return {"accuracy": acc, "f2": f2}

# Training arguments
training_args = TrainingArguments(
    output_dir="./classifier_output", 
    per_device_train_batch_size=16, # divide the training dataset into small subsets
    per_device_eval_batch_size=16,
    num_train_epochs=15,
    evaluation_strategy="epoch",
    logging_steps=10,
    learning_rate=2e-5,
    weight_decay=0.01,
    save_strategy="epoch",
    logging_dir="./logs",
    load_best_model_at_end=True,
    metric_for_best_model="f2",
    greater_is_better=True,
    fp16=True
)

# Trainer
trainer = Trainer(
    model=model,
    args=training_args,
    train_dataset=train_dataset,
    eval_dataset=test_dataset,
    tokenizer=tokenizer,
    compute_metrics=compute_metrics,
    callbacks=[EarlyStoppingCallback(early_stopping_patience=2)]
)

# Train the model
trainer.train()

# Evaluate on the test set
eval_results = trainer.evaluate()
print(f"Test accuracy: {eval_results['eval_accuracy']:.2%}")

# Save the model
model.save_pretrained("engineering_classifierV8")
tokenizer.save_pretrained("engineering_classifierV8")

predictions = trainer.predict(test_dataset)
y_true = predictions.label_ids
y_pred = np.argmax(predictions.predictions, axis=1)

from sklearn.metrics import confusion_matrix, ConfusionMatrixDisplay
import matplotlib.pyplot as plt

cm = confusion_matrix(y_true, y_pred, labels=[0, 1])
disp = ConfusionMatrixDisplay(confusion_matrix=cm, display_labels=["Non-Engineering", "Engineering"])
disp.plot(cmap="Blues")
plt.title("Confusion Matrix")
plt.show()